      "content": "ToolTemplate = require \"../templates/tool\"\n\nmodule.exports = (tool, editor) ->\n  ToolTemplate\n    click: ->\n      editor.activeTool tool\n\n    style: ->\n      \"background-image: url(#{tool.iconURL})\"\n\n    title: ->\n      tool.name\n\n    active: ->\n      \"active\" if editor.activeTool() is tool\n",
